import React from 'react';

const Menu = ({ menuItems, handleIncrease, handleDecrease }) => {
  const handleAvailability = (itemQty) => {
    if (itemQty > 0) {
      return `Available: ${itemQty}`;
    }
    return 'Not Available';
  };

  return (
    <div className="menu">
      <h2>Menu</h2>
      <div className="menu-items">
        {menuItems.map(item => (
          <div key={item.id} className="menu-item">
            <img src={item.image.default} alt={item.name} />
            <div className="item-details">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <p className="price">{item.price}</p>
              <p>{handleAvailability(item.quantity)}</p>
              <div className="quantity-controls">
                <button onClick={() => handleDecrease(item.id)} disabled={item.quantity === 0}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleIncrease(item.id)}>+</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
