import React from 'react';

const Menu = () => {
  const menuItems = [
    { id: 1, name: 'Pasta Carbonara', description: 'Creamy pasta with bacon and Parmesan cheese', price: '₹999', image: require('../menu-img/pasta_carbonara.jpg') },
    { id: 2, name: 'Margherita Pizza', description: 'Classic pizza with tomato sauce and mozzarella cheese', price: '₹599', image: require('../menu-img/margherita_pizza.jpg') },
    // { id: 3, name: 'Caesar Salad', description: 'Fresh salad with romaine lettuce, croutons, and Caesar dressing', price: '₹499', image: require('../menu-img/caesar_salad.jpg') },
    // { id: 4, name: 'Chicken Tikka Masala', description: 'Spicy Indian curry with tender chicken pieces', price: '₹849', image: require('../menu-img/chicken_tikka_masala.jpg') },
    // { id: 5, name: 'Sushi Platter', description: 'Assorted sushi rolls with soy sauce and wasabi', price: '₹1199', image: require('../menu-img/sushi_platter.jpg') },
    // { id: 6, name: 'Cheeseburger', description: 'Juicy beef patty with melted cheese and fresh vegetables', price: '₹699', image: require('../menu-img/cheeseburger.jpg') },
    // { id: 7, name: 'Mango Tango Smoothie', description: 'Refreshing smoothie with fresh mango and yogurt', price: '₹349', image: require('../menu-img/mango_smoothie.jpg') },
    // { id: 8, name: 'Lasagna', description: 'Layers of pasta with Bolognese sauce and cheese', price: '₹899', image: require('../menu-img/lasagna.jpg') },
    // { id: 9, name: 'Vegetable Stir-Fry', description: 'Healthy stir-fried vegetables with a savory sauce', price: '₹599', image: require('../menu-img/vegetable_stir_fry.jpg') },
    // { id: 10, name: 'Chocolate Brownie Sundae', description: 'Warm chocolate brownie with vanilla ice cream and chocolate sauce', price: '₹449', image: require('../menu-img/chocolate_brownie_sundae.jpg') },
    // { id: 11, name: 'Caprese Salad', description: 'Fresh salad with tomatoes, mozzarella, and basil', price: '₹499', image: require('../menu-img/caprese_salad.jpg') },
    // { id: 12, name: 'Grilled Salmon', description: 'Grilled salmon fillet served with lemon and herbs', price: '₹1099', image: require('../menu-img/grilled_salmon.jpg') },
    // { id: 13, name: 'Chicken Caesar Wrap', description: 'Grilled chicken, romaine lettuce, and Caesar dressing wrapped in a tortilla', price: '₹749', image: require('../menu-img/chicken_caesar_wrap.jpg') },
    // { id: 14, name: 'Pad Thai', description: 'Thai-style stir-fried noodles with shrimp and peanuts', price: '₹799', image: require('../menu-img/pad_thai.jpg') },
    // { id: 15, name: 'Tiramisu', description: 'Italian dessert made with layers of coffee-soaked ladyfingers and mascarpone cheese', price: '₹649', image: require('../menu-img/tiramisu.jpg') },
  ];

  return (
    <div className="menu">
      <h2>Menu</h2>
      <div className="menu-items">
        {menuItems.map(item => (
          <div key={item.id} className="menu-item">
            <img src={item.image.default} alt={item.name} />
            <div className="item-details">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <p className="price">{item.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
