import React, { useState } from 'react';
import Menu from './Menu'; // Assuming Menu component is in Menu.jsx or Menu.js

const Available = () => {
  const [menuItems, setMenuItems] = useState([
    { id: 1, name: 'Pasta Carbonara', description: 'Creamy pasta with bacon and Parmesan cheese', price: '₹999', image: require('../menu-img/pasta_carbonara.jpg'), quantity: 3 },
    // Your other menu items here
  ]);

  const handleIncrease = (id) => {
    setMenuItems(menuItems.map(item => item.id === id ? { ...item, quantity: item.quantity + 1 } : item));
  };

  const handleDecrease = (id) => {
    setMenuItems(menuItems.map(item => item.id === id && item.quantity > 0 ? { ...item, quantity: item.quantity - 1 } : item));
  };

  return (
    <div>
      {/* Pass menuItems as prop to Menu component */}
      <Menu menuItems={menuItems} handleIncrease={handleIncrease} handleDecrease={handleDecrease} />
    </div>
  );
};

export default Available;
