import React from 'react';

const Menu = () => {
    const menuItems = [
        { id: 1, name: 'Pasta Carbonara', description: 'Creamy pasta with bacon and Parmesan cheese', price: '₹999', image: '../menu-img/pasta_carbonara.jpg' },
        { id: 2, name: 'Margherita Pizza', description: 'Classic pizza with tomato sauce, mozzarella, and basil', price: '₹799', image: 'margherita_pizza.jpg' },
        { id: 3, name: 'Grilled Salmon', description: 'Grilled salmon fillet served with seasonal vegetables', price: '₹1199', image: 'grilled_salmon.jpg' },
        { id: 4, name: 'Tiramisu', description: 'Classic Italian dessert with layers of coffee-soaked ladyfingers and mascarpone cream', price: '₹499', image: 'tiramisu.jpg' },
        { id: 5, name: 'Chicken Alfredo', description: 'Fettuccine pasta with creamy Alfredo sauce and grilled chicken', price: '₹1299', image: 'chicken_alfredo.jpg' },
        { id: 6, name: 'Caprese Salad', description: 'Fresh mozzarella, tomatoes, and basil drizzled with balsamic glaze', price: '₹699', image: 'caprese_salad.jpg' },
        { id: 7, name: 'Filet Mignon', description: 'Tender filet mignon steak cooked to your preference', price: '₹1599', image: 'filet_mignon.jpg' },
        { id: 8, name: 'Lemon Tart', description: 'Zesty lemon custard in a buttery tart shell, topped with powdered sugar', price: '₹599', image: 'lemon_tart.jpg' },
        { id: 9, name: 'Vegetarian Lasagna', description: 'Layers of pasta, ricotta cheese, spinach, and marinara sauce', price: '₹1099', image: 'vegetarian_lasagna.jpg' },
        { id: 10, name: 'Chocolate Lava Cake', description: 'Warm chocolate cake with a molten chocolate center, served with vanilla ice cream', price: '₹799', image: 'chocolate_lava_cake.jpg' },
      ];
      
  return (
    <div className="menu">
      <h2>Menu</h2>
      <div className="menu-items">
        {menuItems.map(item => (
          <div key={item.id} className="menu-item">
            <img src="" alt="" />
            {/* <img src={`/image/${item.image}`} alt={item.name} /> */}
            <div className="item-details">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <p className="price">{item.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
