import React, { useState } from 'react';

const Available = () => {
//   const [items, setItems] = useState([
//     { id: 1, name: 'Pasta Carbonara', image: require('../menu-img/pasta_carbonara.jpg'), quantity: 3 },
//     { id: 2, name: 'Margherita Pizza', image: require('../menu-img/margherita_pizza.jpg'), quantity: 2 },
//     { id: 3, name: 'Chicken Tikka Masala', image: require('../menu-img/chicken_tikka_masala.jpg'), quantity: 5 },
//     { id: 4, name: 'Caesar Salad', image: require('../menu-img/caesar_salad.jpg'), quantity: 1 },
//     { id: 5, name: 'Sushi Platter', image: require('../menu-img/sushi_platter.jpg'), quantity: 4 },
//     { id: 6, name: 'BBQ Ribs', image: require('../menu-img/bbq_ribs.jpg'), quantity: 2 },
//     { id: 7, name: 'Vegetable Stir-Fry', image: require('../menu-img/vegetable_stir_fry.jpg'), quantity: 3 },
//     { id: 8, name: 'Tiramisu', image: require('../menu-img/tiramisu.jpg'), quantity: 2 },
//     { id: 9, name: 'Beef Burger', image: require('../menu-img/beef_burger.jpg'), quantity: 4 },
//     { id: 10, name: 'Pad Thai Noodles', image: require('../menu-img/pad_thai_noodles.jpg'), quantity: 3 },
//     { id: 11, name: 'Caprese Salad', image: require('../menu-img/caprese_salad.jpg'), quantity: 1 },
//     { id: 12, name: 'Chicken Alfredo', image: require('../menu-img/chicken_alfredo.jpg'), quantity: 5 },
//     { id: 13, name: 'Mushroom Risotto', image: require('../menu-img/mushroom_risotto.jpg'), quantity: 2 },
//     { id: 14, name: 'Cheese Platter', image: require('../menu-img/cheese_platter.jpg'), quantity: 3 },
//     { id: 15, name: 'Chocolate Fondue', image: require('../menu-img/chocolate_fondue.jpg'), quantity: 4 },
//     { id: 16, name: 'Lasagna', image: require('../menu-img/lasagna.jpg'), quantity: 5 },
//   ]);

  const increaseQuantity = (id) => {
    const updatedItems = items.map(item => {
      if (item.id === id) {
        return { ...item, quantity: item.quantity + 1 };
      }
      return item;
    });
    setItems(updatedItems);
  };

  const decreaseQuantity = (id) => {
    const updatedItems = items.map(item => {
      if (item.id === id && item.quantity > 0) {
        return { ...item, quantity: item.quantity - 1 };
      }
      return item;
    });
    setItems(updatedItems);
  };

  return (
    <div className="items-container">
      {items.map(item => (
        <div key={item.id} className="card">
          <img src={item.image.default} alt={item.name} className="card-img-top" />
          <div className="card-body">
            <h5 className="card-title">{item.name}</h5>
            <p className="card-text">Quantity: {item.quantity}</p>
            <div className="quantity-controls">
              <button onClick={() => decreaseQuantity(item.id)} className="quantity-btn">-</button>
              <button onClick={() => increaseQuantity(item.id)} className="quantity-btn">+</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Available;
