import React, { useState } from 'react';

const Available = () => {
  const [menuItems, setMenuItems] = useState();

  const handleIncrease = (id) => {
    setMenuItems(menuItems.map(item => item.id === id ? { ...item, quantity: item.quantity + 1 } : item));
  };

  const handleDecrease = (id) => {
    setMenuItems(menuItems.map(item => item.id === id && item.quantity > 0 ? { ...item, quantity: item.quantity - 1 } : item));
  };

  return (
    <div>
      {menuItems.map(item => (
        <div key={item.id}>
          <h3>{item.name}</h3>
          <p>{item.description}</p>
          <p>{item.price}</p>
          <img src={item.image} alt={item.name} width={100} />
          <p>Quantity: {item.quantity}</p>
          <p>Status: {item.quantity > 0 ? 'Available' : 'Not Available'}</p>
          <button onClick={() => handleIncrease(item.id)}>Increase</button>
          <button onClick={() => handleDecrease(item.id)}>Decrease</button>
        </div>
      ))}
    </div>
  );
};

export default Available;
