import React from 'react';
// import pastaCarbonaraImg from '../menu-img/';
import margheritaPizzaImg from '../menu-img/margherita_pizza.jpg';
// Import other images as needed

const Menu = () => {
  const menuItems = [
    { id: 1, name: 'Pasta Carbonara', description: 'Creamy pasta with bacon and Parmesan cheese', price: '₹999', image: '/menu-img/pasta_carbonara.jpg'},
    { id: 2, name: 'Margherita Pizza', description: 'Classic pizza with tomato sauce, mozzarella, and basil', price: '₹799', image: margheritaPizzaImg },
    // Add other menu items
  ];

  return (
    <div className="menu">
      <h2>Menu</h2>
      <div className="menu-items">
        {menuItems.map(item => (
          <div key={item.id} className="menu-item">
            <img src={item.image} alt={item.name} />
            <div className="item-details">
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              <p className="price">{item.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
