import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

function Order() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    place: '',
    order: '',
    quantity: '',
    message: '',
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("https://sheet.best/api/sheets/fded5555-ba08-4e0e-908c-9b6b47fc356c", formData)
      .then((response) => {
        console.log(response);
        setFormData({
          name: '',
          email: '',
          phone: '',
          place: '',
          order: '',
          quantity: '',
          message: '',
        });
        alert('Order placed!');
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('Order failed!');
      });
  };

  return (
    <section id="order" className="order-section">
      <div className="container" data-aos="fade-up">
        <div className="row">
          <div className="col-md-8 offset-md-2 text-center">
            <h2 className="text-primary">Place Your Order</h2>
            <p className="mb-5">Fill out the form below to place your order.</p>
          </div>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="form-row">
            <div className="col-lg-4 col-md-6 form-group">
              <input
                type="text"
                name="name"
                className="form-control"
                placeholder="Your Name"
                value={formData.name}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input
                type="email"
                name="email"
                className="form-control"
                placeholder="Your Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input
                type="tel"
                name="phone"
                className="form-control"
                placeholder="Your Phone"
                value={formData.phone}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input
                type="text"
                name="place"
                className="form-control"
                placeholder="Delivery Address"
                value={formData.place}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input
                type="text"
                name="order"
                className="form-control"
                placeholder="Order Details"
                value={formData.order}
                onChange={handleChange}
                required
              />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input
                type="number"
                name="quantity"
                className="form-control"
                placeholder="Quantity"
                value={formData.quantity}
                onChange={handleChange}
                required
              />
            </div>
          </div>
          <div className="form-group">
            <textarea
              name="message"
              className="form-control"
              placeholder="Additional Instructions"
              value={formData.message}
              onChange={handleChange}
            ></textarea>
          </div>
          <button type="submit" className="btn btn-primary float-right mt-3">Place Order</button>
        </form>
      </div>
    </section>
  );
}

export default Order;
