import React from 'react';
function ReservationForm() {
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Submitted");

    const formData = new FormData(e.target);
    const data = {};
    for (const [key, value] of formData.entries()) {
      data[key] = value;
    }

    fetch('https://script.google.com/macros/s/AKfycbzgRPLzkmwU9XMaYHAeUidPXW6iW3hYyY2wix8bW0_j-RIOnMys-dOlMiPgZXe3X1WX/exec', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: {
        'Content-Type': 'application/json'
      }
    })
   .then(response => response.text())
   .then(message => console.log(message));
  };

  return (
    <section id="reservation" className="reservation-section">
      <div className="container" data-aos="fade-up">
        <div className="row">
          <div className="col-md-8 offset-md-2 text-center">
            <h2 className="text-primary">Book A Table</h2>
            <p className="mb-5">Restaurant reservation table is created where users can book online restaurant tables on their own.</p>
          </div>
        </div>
        <form className="form" onSubmit={(e) => handleSubmit(e)}>
          <div className="form-row">
            <div className="col-lg-4 col-md-6 form-group">
              <input type="text" name="name" className="form-control" placeholder="Your Name" required />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input type="email" name="email" className="form-control" placeholder="Your Email" required />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input type="tel" name="phone" className="form-control" placeholder="Your Phone" required />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input type="date" name="date" className="form-control" required />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input type="time" name="time" className="form-control" required />
            </div>
            <div className="col-lg-4 col-md-6 form-group">
              <input type="number" name="people" className="form-control" placeholder="No. of people" required />
            </div>
          </div>
          <div className="form-group">
            <textarea name="message" className="form-control" placeholder="Message" ></textarea>
          </div>
          <button type="submit" className="btn btn-primary float-right mt-3">Book a Table</button>
        </form>
      </div>
    </section>
  );
}

export default ReservationForm;
